package tech.themeko.plasmoautomute;import org.bukkit.configuration.file.FileConfiguration;import org.bukkit.entity.Player;import org.bukkit.scheduler.BukkitRunnable;import su.plo.voice.PlasmoVoiceAPI;public class LoopTask extends BukkitRunnable { private FileConfiguration config;private PlasmoVoiceAPI plasmoVoiceAPI;public LoopTask(PlasmoAutoMute plugin, PlasmoVoiceAPI plasmoVoiceAPI) {this.config = plugin.getConfig(); this.plasmoVoiceAPI = plasmoVoiceAPI;}@Override public void run() {for (Player player : plasmoVoiceAPI.getConnectedPlayers()) {if (player.hasPermission(config.getString("permission"))) plasmoVoiceAPI.mute(player.getUniqueId(), 1, PlasmoVoiceAPI.DurationUnit.DAYS, "komar", true);}}}